'use strict';
var qtools = require('qtools'),
	qtools = new qtools(module),
	events = require('events'),
	util = require('util');

//START OF moduleFunction() ============================================================

var moduleFunction = function(args) {
	events.EventEmitter.call(this);
	this.forceEvent = forceEvent;
	this.args=args;

	qtools.validateProperties({
		subject: args,
		targetScope: this, //will add listed items to targetScope
		propList: [
			{
				name: 'id',
				optional: false
			},
			{
				name: 'requestToken',
				optional: false
			}
		]
	});

	var self = this,
		forceEvent = function(eventName, outData) {
			this.emit(eventName, {
				eventName: eventName,
				data: outData
			});
		};


	//PRIVATE FUNCTIONS ====================================

	var executeAuthorization=function(){
	
	var dataFileDirectory=global.localEnvironment.get('dataFileDirectory');
		self.profileData={
			id:self.id,
			dataSource:{
				type: 'file',
				location: dataFileDirectory+'/testClient/',
				fileExtension:'uff'
			},
			receivingSchema:'expressbook'
		}
// 		self.errorResultData={status:-1, message:'bad credentials'};
// 		return -1;
		return 1;
	}


	//METHODS AND PROPERTIES ====================================

	this.auth=function(){
		return executeAuthorization();
	};
	
	this.errorResult=function(){
		return self.errorResultData;
	};
	
	this.profile=function(){
		return self.profileData;
	};


	//INITIALIZATION ====================================

	this.args = args;

	return this;
};

//END OF moduleFunction() ============================================================

util.inherits(moduleFunction, events.EventEmitter);
module.exports = moduleFunction;
